import { Link } from "react-router-dom";
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import '../App.css';

import { useState, useEffect } from 'react';

import * as React from 'react';
import Box from '@mui/material/Box';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import Button from '@mui/material/Button';
import CloseIcon from '@mui/icons-material/Close'

const [open, setOpen] = useState(true);

const StyledTableCell = styled(TableCell)(({ theme }) => ({
    [`&.${tableCellClasses.head}`]: {
      backgroundColor: theme.palette.common.white,
      color: theme.palette.common.black,
    },
    [`&.${tableCellClasses.body}`]: {
      fontSize: 14,
    },
  }));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.grey[400],
    },
    // hide last border
    '&:last-child td, &:last-child th': {
      border: 0,
    },
  }));

const PrendasList = ({ prendas }) => {
    function sayHello() {
        
        return (
            <Box sx={{ width: '100%' }}>
              <Collapse in={open}>
                <Alert
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false);
                      }}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>
                  }
                  sx={{ mb: 2 }}
                >
                  Close me!
                </Alert>
              </Collapse>
              <Button
                disabled={open}
                variant="outlined"
                onClick={() => {
                  setOpen(true);
                }}
              >
                Re-open
              </Button>
            </Box>
            )
      }
    return (
        <div  id='containerGrid'>
         <TableContainer component={Paper} style={{ width: '50%', margin: 'auto' }}>
                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                            <TableHead>
                            <TableRow>
                                <StyledTableCell align="right" >ID</StyledTableCell>
                                <StyledTableCell align="right">Descripcion</StyledTableCell>
                                <StyledTableCell align="right">Tipo</StyledTableCell>
                                <StyledTableCell align="right">Precio Base</StyledTableCell>
                                <StyledTableCell align="right"></StyledTableCell>
                            </TableRow>
                            </TableHead>
                            <TableBody>
            {prendas.map(prendas => {
                return (
                                <StyledTableRow>
                                <StyledTableCell align="right">{prendas.id}</StyledTableCell>
                                <StyledTableCell align="right">{prendas.descripcion}</StyledTableCell>
                                <StyledTableCell align="right">{prendas.tipo}</StyledTableCell>
                                <StyledTableCell align="right">{prendas.precioBase}</StyledTableCell>
                                <StyledTableCell> <Link to={`/prendas/${prendas.id}`}>Editar</Link> </StyledTableCell>
                                <StyledTableCell> <Button onClick={sayHello}>Eliminar</Button></StyledTableCell>
                                </StyledTableRow>
                )
            })}
                            </TableBody>
                        </Table>
        </TableContainer>
        </div>
    );
}
export default PrendasList;
